lista = []for i in range (10):    lista.append(float(input("Digite um numero: ")))maior_numero = max(lista)menor_numero = min(lista)lista.remove(maior_numero)lista.remove(menor_numero)segundo_maior_numero = max(lista)segundo_menor_numero = min(lista)print(f"O segundo maior número da lista é {segundo_maior_numero}")print(f"O segundo menor número da lista é {segundo_menor_numero}")        